pragma solidity ^0.5.0;

// import "openzeppelin-solidity/contracts/math/SafeMath.sol";

contract HideAndSeek {

  // using SafeMath for uint256;

  uint8 NUM_ROOMS = 8;
  uint PRICE_TO_HIDE = 20 finney;
  uint8 numRoomsOccupied = 0;
  mapping (uint8 => address) rooms;


  function getEmptyRoomIds() public view returns (uint8[] memory) {
    uint8[] memory ids = new uint8[](numRoomsOccupied);
    uint8 counter = 0;
    for (uint8 i = 0; i < NUM_ROOMS; i++) {
      if (rooms[i] == address(0)) {
        ids[counter] = i;
        counter++;
      } 
    }
    return ids;
  }

  function hideInRoom(uint8 roomId) public payable {
    require(msg.value >= PRICE_TO_HIDE);
    require(roomId < NUM_ROOMS);
    require(rooms[roomId] == address(0));
    rooms[roomId] = msg.sender;
    numRoomsOccupied++;
    if (numRoomsOccupied == NUM_ROOMS) {
      chooseWinner();
    }
  }

  function chooseWinner() internal {
    // TODO: Randomly select winner, and transfer winnings
    for (uint8 i = 0; i < NUM_ROOMS; i++) {
      rooms[i] = address(0);
    }
    numRoomsOccupied = 0;
  }


}
